mapper.select:
SELECT a.*, b.name, u.nick_name, group_concat( t.NAME SEPARATOR ',' ) AS tagNames FROM blog_article a JOIN blog_category b ON a.category_id = b.id JOIN blog_article_tag atg ON a.id = atg.article_id JOIN blog_tags t ON atg.tag_id = t.id JOIN `cxblog-cloud`.sys_user u ON a.user_id=u.user_id
a.title like concat('%',#{title},'%')
and a.category_id=#{categoryId}
and a.is_publish=#{isPublish}
and atg.tag_id=#{tagId}
GROUP BY a.id order by a.is_stick desc ,a.create_time desc

mapper.update:
update blog_article
set is_stick = #{article.isStick}
where id = #{article.id}

mapper.select:
SELECT a.*, ui.nickname AS username, b.NAME AS categoryName, b.id AS categoryId FROM blog_article a left JOIN blog_category b ON a.category_id = b.id left JOIN `cxblog-cloud`.sys_user u ON a.user_id = u.user_id left join blog_user_userinfo uui on u.user_id = uui.user_id left join blog_user_info ui on uui.user_info_id = ui.id where a.user_id =#{userId}
and a.is_publish = #{type}
order by a.create_time desc
